Ext.define('cartera.view.InstitucionesTabViewController8', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.bancostab',

    onBtnAgregarBClick: function(button, e, eOpts) {
        var FormBancos = Ext.create('cartera.view.BancosWin');
        FormBancos.show();
    },

    onBtnBorrarBClick: function(button, e, eOpts) {
        var record = Ext.getCmp('GridBancos').getSelectionModel().getSelection()[0];
        if(record !== undefined){
            var estatus = Ext.getCmp('GridBancos').getSelectionModel().getSelection()[0].data.estatus;
            var nombre = Ext.getCmp('GridBancos').getSelectionModel().getSelection()[0].data.nombre;
            console.log(estatus);
            if(estatus === 1){
                Ext.MessageBox.show({
                    title : 'Desactivar Banco',
                    buttons : Ext.MessageBox.YESNO,
                    buttonText:{
                        yes:'Sí',
                        no:'No',
                        cancel:'Cancelar'
                    },
                    msg : '¿Desea Desactivar a '+nombre+'?',
                    icon : Ext.Msg.WARNING,
                    fn : function(btn)
                    {
                        if (btn == 'yes')
                        {
                            var values= {'estatus': 0};
                            record.set(values);
                            Ext.getStore('BancosStore').sync({
                                callback: function(records, operation, success)
                                {
                                    Ext.MessageBox.show({
                                        title : 'Desactivar Banco',
                                        buttons : Ext.MessageBox.OK,
                                        msg : 'Banco desactivado correctamente',
                                        icon : Ext.Msg.INFO
                                    });
                                }
                            });
                        }
                    }
                });
            }
            else{
                Ext.MessageBox.show({
                    title : 'Activar Banco',
                    buttons : Ext.MessageBox.YESNO,
                    buttonText:{
                        yes:'Sí',
                        no:'No',
                        cancel:'Cancelar'
                    },
                    msg : '¿Desea Activar a '+nombre+'?',
                    icon : Ext.Msg.WARNING,
                    fn : function(btn)
                    {
                        if (btn == 'yes')
                        {
                            var values= {'estatus': 1};
                            record.set(values);
                            Ext.getStore('BancosStore').sync({
                                callback: function(records, operation, success)
                                {
                                    Ext.MessageBox.show({
                                        title : 'Activar Banco',
                                        buttons : Ext.MessageBox.OK,
                                        msg : 'Banco correctamente',
                                        icon : Ext.Msg.INFO
                                    });
                                }
                            });
                        }
                    }
                });
            }
        }
        else{
            Ext.MessageBox.show({
                title : 'Información',
                buttons : Ext.MessageBox.OK,
                msg : 'Seleccione registro.',
                icon : Ext.Msg.INFO
            });

        }
    },

    onBtnModificarBClick: function(button, e, eOpts) {
        records = Ext.getCmp('GridBancos').getSelectionModel().getSelection();
        if(records.length > 0){
            var WinEstatusIngreso = Ext.create('cartera.view.BancosWin'),
                EditForm = WinEstatusIngreso.down('form'),
                record = records[0],
                form   = Ext.getCmp('formBancos');

            WinEstatusIngreso.setTitle('Modificar Banco');
            form.getForm().loadRecord(record);



        }else{
            Ext.MessageBox.show({
                title : 'Advertencia',
                buttons : Ext.MessageBox.OK,
                msg : 'Seleccione registro.',
                icon : Ext.Msg.ERROR

            });
        }
    },

    onBtnActualizarBClick: function(button, e, eOpts) {
        Ext.getStore('BancosStore').load();

    },

    onGridcolumnAfterRender: function(component, eOpts) {
        Ext.getStore('BancosStore').load({
            params:{
                'id_ins': Ext._id_institucion
            },
        });

    }

});
