Ext.define('cartera.view.InstitucionesTabViewController9', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.datosfacttab',

    onBtnActualizarFamiliasClick: function(button, e, eOpts) {
        Ext.getStore('FamiliasStore').load({
            params:{
                'id_ins': Ext._id_institucion,
                'id_ciclo_escolar': Ext._id_ciclo_escolar
            },
        });


    },

    onTextfieldKeypress: function(textfield, e, eOpts) {

    },

    onTextfieldChange: function(field, newValue, oldValue, eOpts) {
        //var rdSolicitada = Ext.getCmp('rdSolicitada').getValue();
        //var rdRefrendo = Ext.getCmp('rdRefrendo').getValue();
        //var rdTodos = Ext.getCmp('rdTodos').getValue();
        var busqueda = Ext.getCmp('txtBusFamilia').getValue().toUpperCase();

        if(newValue === ''){

            Ext.getStore('FamiliasFacStore').clearFilter();

        }else {
            Ext.getStore('FamiliasFacStore').clearFilter();
            Ext.getStore('FamiliasFacStore').filter({
                filterFn: function(f) {
                    return f.get('ap_paterno').indexOf(busqueda) > -1;
                }
            });
        }
    },

    onGridcolumnAfterRender: function(component, eOpts) {

        Ext.getStore('FamiliasFacStore').load({
            params:{
                'id_ins': Ext._id_institucion,
                'id_ciclo_escolar': Ext._id_ciclo_escolar
            },
        });
    },

    onGridFamiliasSelect: function(rowmodel, record, index, eOpts) {



        Ext.getStore('DatosFactStore').load({
            params:{
                'id_familia': record.data.id_familia
            }
        });



    },

    onBtnAgregarDatosFactClick: function(button, e, eOpts) {

        records = Ext.getCmp('GridFamilias').getSelectionModel().getSelection();
        if(records.length > 0){
            var WinDatosFact = Ext.create('cartera.view.DatosFactWin'),
                EditForm = WinDatosFact.down('form'),
                record = records[0],
                form = Ext.getCmp('formDatosFact');

            WinDatosFact.setTitle('Agregar Datos Facturación');
            form.getForm().loadRecord(record);
            //Ext.getCmp('institucionescmb').disabled=true;

        }else{
            Ext.MessageBox.show({
                title : 'Advertencia',
                buttons : Ext.MessageBox.OK,
                msg : 'Seleccione registro.',
                icon : Ext.Msg.ERROR

            });
        }
    },

    onBtnBorrarDatosFactClick: function(button, e, eOpts) {
        var record = Ext.getCmp('GridDatosFact').getSelectionModel().getSelection()[0];
        if(record !== undefined){
            var estatus = Ext.getCmp('GridDatosFact').getSelectionModel().getSelection()[0].data.estatus;
            console.log(estatus);
            if(estatus === 1){
                Ext.MessageBox.show({
                    title : 'Desactivar Datos de Facturación',
                    buttons : Ext.MessageBox.YESNO,
                    buttonText:{
                        yes:'Sí',
                        no:'No',
                        cancel:'Cancelar'
                    },
                    msg : '¿Desea Desactivar el Dato Seleccionado?',
                    icon : Ext.Msg.WARNING,
                    fn : function(btn)
                    {
                        if (btn == 'yes')
                        {
                            var values= {'estatus': 0};
                            record.set(values);
                            Ext.getStore('DatosFactStore').sync({
                                callback: function(records, operation, success)
                                {
                                    Ext.MessageBox.show({
                                        title : 'Desactivar Dato de Facturación',
                                        buttons : Ext.MessageBox.OK,
                                        msg : 'Dato desactivado correctamente',
                                        icon : Ext.Msg.INFO
                                    });
                                }
                            });
                        }
                    }
                });
            }
            else{
                Ext.MessageBox.show({
                    title : 'Activar Dato de Facturación',
                    buttons : Ext.MessageBox.YESNO,
                    buttonText:{
                        yes:'Sí',
                        no:'No',
                        cancel:'Cancelar'
                    },
                    msg : '¿Desea Activar el Dato Seleccionado?',
                    icon : Ext.Msg.WARNING,
                    fn : function(btn)
                    {
                        if (btn == 'yes')
                        {
                            var values= {'estatus': 1};
                            record.set(values);
                            Ext.getStore('DatosFactStore').sync({
                                callback: function(records, operation, success)
                                {
                                    Ext.MessageBox.show({
                                        title : 'Activar Dato de Facturación',
                                        buttons : Ext.MessageBox.OK,
                                        msg : 'Dato activado correctamente',
                                        icon : Ext.Msg.INFO
                                    });
                                }
                            });
                        }
                    }
                });
            }
        }
        else{
            Ext.MessageBox.show({
                title : 'Información',
                buttons : Ext.MessageBox.OK,
                msg : 'Seleccione registro.',
                icon : Ext.Msg.INFO
            });

        }
    },

    onBtnModificarDatosFactClick: function(button, e, eOpts) {
        records = Ext.getCmp('GridDatosFact').getSelectionModel().getSelection();
        if(records.length > 0){
            var WinDatosFact = Ext.create('cartera.view.DatosFactWin'),
                EditForm = WinDatosFact.down('form'),
                record = records[0],
                form = Ext.getCmp('formDatosFact');

            WinDatosFact.setTitle('Modificar Datos Facturación');
            form.getForm().loadRecord(record);
            //Ext.getCmp('institucionescmb').disabled=true;

        }else{
            Ext.MessageBox.show({
                title : 'Advertencia',
                buttons : Ext.MessageBox.OK,
                msg : 'Seleccione registro.',
                icon : Ext.Msg.ERROR

            });
        }
    },

    onBtnActualizarDatosFactClick: function(button, e, eOpts) {
        var id_familia = Ext.getCmp('GridFamilias').getSelectionModel().getSelection()[0].data.id_familia;

        Ext.getStore('DatosFactStore').load({
            params:{
                'id_familia': id_familia
            },
        });
    },

    onGridcolumnAfterRender111: function(component, eOpts) {

    }

});
