Ext.define('cartera.view.EditarPlazaWindowViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.editarplazawindow',

    onbtnGuardar: function(button, e, eOpts) {
        //console.log("HOLIS");
        var ctrl = this,
            editarPlazaForm = ctrl.view.down("#formPlaza").getForm(),
            plazasStore = Ext.getStore("PlazasStore");

        if (editarPlazaForm.isValid()) {
            editarPlazaForm.updateRecord();
            plazasStore.sync();
            ctrl.view.destroy();
        }

    },

    onbtnCancelar: function(button, e, eOpts) {
        this.view.destroy();
    }

});
