Ext.define('cartera.view.InstitucionesWinViewController9', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.usocfdiwin',

    onCmbUsoUCChange: function(field, newValue, oldValue, eOpts) {
        var lengthRecord = field.lastSelectedRecords;
        // form = Ext.getCmp('formUsoCFDI'),
        // formCmb = Ext.getCmp('comboForm');
        var clave = field.lastSelectedRecords[0].data.clave;
        var id_cfdi = field.lastSelectedRecords[0].data.id;


        if(lengthRecord !== null){
            Ext.getCmp('txtClaveUC').setValue(clave);
            Ext.getCmp('uso_cfdi_id').setValue(id_cfdi);


        }

    },

    onbtnGuardarUC: function(button, e, eOpts) {
        var win = button.up('window'),
            form   = Ext.getCmp('formUsoCFDI'),
            record = form.getRecord(),
            values = form.getValues();

        console.log(values.id_banco);
        if(form.isValid()){
            if(values.id_banco>0){

                form.submit({
                    params: Ext.JSON.encode(values),
                    url: 'api/usocfdi/actualiza',
                    waitMsg: 'Procesando información...',
                    success: function() {


                        Ext.MessageBox.show({
                            title : 'Información',
                            buttons : Ext.MessageBox.OK,
                            msg : 'Estatus '+values.uso+' ha sido actualizado.',
                            icon : Ext.Msg.INFO

                        });
                        Ext.getStore('UsoCFDIStore').load({
                            params:{
                                'id_ins': Ext._id_institucion
                            },
                        });


                        win.close();
                    },
                    failure: function() {

                    }
                });
            }else{
                //Agregar Estatus Ingreso.
                if(Ext.getStore('UsoCFDIStore').findExact('clave', values.clave)!=-1){
                    Ext.MessageBox.show({
                        title : 'Advertencia',
                        buttons : Ext.MessageBox.OK,
                        msg : 'Ya Existe el uso: '+values.uso+'. Verifique...',
                        icon : Ext.Msg.ERROR
                    });
                }else{
                    // var recordLogin = Ext.getStore('StoreLogin').data.items[0];
                    //values.id_usuarioAct = recordLogin.data.id_usuario;
                    record = Ext.create('cartera.model.UsoCFDIModel');
                    console.log(record);
                    record.set(values);
                    //Ext.getStore('FormasPagoStore').add(record);
                    //Ext.getStore('FormasPagoStore').sync();
                    //Ext.getCmp('id_insFP').setValue(Ext._id_institucion);
                    form.submit({
                        params: Ext.JSON.encode(values),
                        url: 'api/usocfdi/create',
                        waitMsg: 'Procesando información...',
                        success: function() {


                            Ext.MessageBox.show({
                                title : 'Información',
                                buttons : Ext.MessageBox.OK,
                                msg : 'Uso CFDI '+values.uso+' ha sido agregado.',
                                icon : Ext.Msg.INFO

                            });
                            Ext.getStore('UsoCFDIStore').load({
                                params:{
                                    'id_ins': Ext.getCmp('id_institucionUC').getValue()
                                },
                            });

                            win.close();
                        },
                        failure: function() {
                            //console.log('FAILURE CAPTURA INSTITUCION');
                        }
                    });


                }
            }

        }else{
            //Si el formulario no es válido
            Ext.MessageBox.show({
                title : 'Atencion',
                buttons : Ext.MessageBox.OK,
                msg : 'Ingrese todos los campos obligatorios',
                icon : Ext.Msg.ERROR
            });
        }
    },

    onbtnCancelarUC: function(button, e, eOpts) {
        button.up('window').close();
    },

    onUsoCFDIWinAfterRender: function(component, eOpts) {
        Ext.getCmp('id_institucionUC').setValue(Ext._id_institucion);


    }

});
